import { scales } from './consts';

const Home = () => {

  const sound = (freq:number) => {
    const audioCtx = new AudioContext();
    var oscillator = audioCtx.createOscillator();
    // var envelopeGen = audioCtx.createGain();
    // oscillator.frequency.value = freq;
    // oscillator.connect(audioCtx.destination);
    oscillator.start();
    // oscillator.stop(1)
  }

  return (
    <>
    <h1>Web Tuner</h1>

    <button onClick={() => sound(scales['E1'])}>
      E
    </button>
    <button onClick={() => sound(scales['A1'])}>
      A
    </button>
    <button onClick={() => sound(scales['D2'])}>
      D
    </button>
    <button onClick={() => sound(scales['G2'])}>
      G
    </button>
    </>
  )
};

export default Home;
